import { useState } from "react";
import { useSignup } from "../hooks/useSignup";
import { StyleSheet, View, Text, TextInput, TouchableOpacity } from "react-native";

const Signup = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { signup, error, isLoading } = useSignup();

  const generatePassword = () => {
    const length = 12;
    const lower = "abcdefghijklmnopqrstuvwxyz";
    const upper = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
    const numbers = "0123456789";
    const special = "!@#$%^&*()_+{}[]<>?";
    const allChars = lower + upper + numbers + special;

    let generatedPassword = "";

    generatedPassword += lower[Math.floor(Math.random() * lower.length)];
    generatedPassword += upper[Math.floor(Math.random() * upper.length)];
    generatedPassword += numbers[Math.floor(Math.random() * numbers.length)];
    generatedPassword += special[Math.floor(Math.random() * special.length)];

    for (let i = 4; i < length; i++) {
      generatedPassword += allChars[Math.floor(Math.random() * allChars.length)];
    }

    generatedPassword = generatedPassword.split('').sort(() => 0.5 - Math.random()).join('');

    setPassword(generatedPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await signup(email, password);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Sign Up</Text>

      <Text style={styles.label}>Email address:</Text>
      <TextInput 
        style={styles.input}
        keyboardType="email-address"
        onChangeText={setEmail}
        value={email}
      />

      <Text style={styles.label}>Password:</Text>
      <TextInput 
        style={styles.input}
        secureTextEntry
        onChangeText={setPassword}
        value={password}
      />

      <TouchableOpacity style={styles.buttonGold} onPress={generatePassword}>
        <Text style={styles.buttonText}>Generate Password</Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.buttonBlue} onPress={handleSubmit} disabled={isLoading}>
        <Text style={styles.buttonText}>Sign up</Text>
      </TouchableOpacity>

      {error && <Text style={styles.error}>{error}</Text>}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#4169e1", // Royal Blue
    padding: 20,
    borderRadius: 10,
    width: "90%",
    alignSelf: "center",
  },
  header: {
    fontSize: 24,
    fontWeight: "bold",
    color: "#ffffff", // White Text
    marginBottom: 20,
    textAlign: "center",
  },
  label: {
    fontSize: 16,
    color: "#ffffff",
    marginBottom: 5,
  },
  input: {
    backgroundColor: "#ffffff",
    color: "#000000",
    padding: 10,
    borderRadius: 5,
    marginBottom: 15,
  },
  buttonGold: {
    backgroundColor: "#FFD700", // Gold Button
    padding: 12,
    borderRadius: 5,
    alignItems: "center",
    marginBottom: 10,
  },
  buttonBlue: {
    backgroundColor: "#001F3F", // Dark Royal Blue Button
    padding: 12,
    borderRadius: 5,
    alignItems: "center",
  },
  buttonText: {
    color: "#ffffff",
    fontSize: 16,
    fontWeight: "bold",
  },
  error: {
    color: "#ff0000",
    marginTop: 10,
  },
});

export default Signup;
